mapper.update:
UPDATE board SET
hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no,subject,content,TO_CHAR(regdate,'RRRR-MM-DD') as dbday,hit
FROM board 
WHERE no=#{no}

mapper.update:
UPDATE board SET
subject=#{subject},content=#{content}
WHERE no=#{no}

mapper.select:
SELECT COUNT(*) 
FROM board
WHERE bno=#{bno}

SELECT group_id,group_step,group_tab 
FROM board
WHERE no=#{no}

mapper.update:
UPDATE board SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE board SET
depth=depth+1
WHERE no=#{no}

UPDATE board_Reply SET
content=#{content}
WHERE no=#{no}

UPDATE board_Reply SET
content=#{content}
WHERE no=#{no}

UPDATE board_Reply SET
depth=depth-1
WHERE no=#{no}
