mapper.select:
	  
SELECT no, id, area, dtype, dsex, dkg, dcolor, dcharacter,category, tel, dname, dage,FILENAME,FILESIZE, dbday, num
FROM (SELECT no, id, area,  dtype, dsex, dkg, dcolor, dcharacter,category, tel, dname, dage,FILENAME,FILESIZE, dbday, rownum as num
	FROM(SELECT no, id, area,  dtype, dsex, dkg, dcolor, dcharacter,category, tel, dname, dage,FILENAME,FILESIZE, TO_CHAR(regdate, 'YYYY-MM-DD') as dbday
	FROM board_dog b)		
)WHERE num BETWEEN #{start} AND #{end}
ORDER BY no DESC

SELECT no, id, area, dtype, dsex, dkg, dcolor,category, dcharacter, tel, dname, dage, filename,TO_CHAR(regdate,'RRRR-MM-DD') as dbday
FROM board_dog 
WHERE no=#{no}

mapper.update:
UPDATE board_dog SET
area=#{area}, dtype=#{dtype}, dsex=#{dsex}, dkg=#{dkg}, dcolor=#{dcolor}, category=#{category},
 dcharacter=#{dcharacter}, tel=#{tel}, dname=#{dname}, filename=#{filename}, filesize=#{filesize},
 dage=#{dage}
WHERE no=#{no}

mapper.select:
SELECT no, id, area, dtype, dsex, dkg, dcolor, dcharacter,category, tel, dname, dage,FILENAME,FILESIZE, dbday, num
