mapper.select:
SELECT name
FROM hospital
WHERE no = #{no}

SELECT dateno 
FROM hospital
WHERE no=#{no}

SELECT timeno 
FROM dateInfo
WHERE dno=#{dno}

SELECT time
FROM timeInfo
WHERE timeno=#{timeno}

SELECT name
FROM member
WHERE id=#{id}

SELECT dno,dname, dtype, TO_CHAR(dbirth, 'YYYY-MM-DD') as dbirth,dsex
FROM dog
WHERE id=#{id}

SELECT no, reception_date, name, dname, reserve_date, reserve_time, state, hosname, num FROM ( SELECT no, reception_date, name, dname, reserve_date, reserve_time, state, hosname, rownum as num FROM( SELECT no, TO_CHAR(reception_date, 'YYYY-MM-DD') as reception_date, (SELECT name FROM member WHERE id = (SELECT id FROM dog WHERE dno = dogno)) as name, (SELECT dname FROM dog WHERE dno = dogno) as dname, reserve_date, reserve_time, state, (SELECT name FROM hospital WHERE no = hosno) as hosname FROM reserve
reception_date BETWEEN TO_DATE(#{startDate},'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD') + 0.99999
AND dogno IN (SELECT dno FROM dog WHERE id = #{id})
AND dogno = (SELECT dno FROM dog WHERE dno = #{dno})
) ) WHERE num BETWEEN #{start} AND #{end}

SELECT no, caredate, prescription, doctor, dogname, hospitalname, num FROM ( SELECT no, caredate, prescription, doctor, dogname, hospitalname, rownum as num FROM( SELECT no, TO_CHAR(caredate, 'YYYY-MM-DD') as caredate, prescription, doctor, (SELECT dname FROM dog WHERE dno = dogno) as dogname, (SELECT name FROM hospital WHERE no = (SELECT hosno FROM reserve WHERE no = resno)) as hospitalname FROM carechart
caredate BETWEEN TO_DATE(#{startDate},'YYYY-MM-DD') AND TO_DATE(#{endDate}, 'YYYY-MM-DD') + 0.99999
AND dogno IN (SELECT dno FROM dog WHERE id = #{id})
AND dogno = (SELECT dno FROM dog WHERE dno = #{dno})
) ) WHERE num BETWEEN #{start} AND #{end}
