mapper.select:
SELECT no, category, id, subject, dbday, hit, group_id, group_step, group_tab, root, depth, count, num 
FROM (SELECT no, category, id, subject, dbday, hit, group_id, group_step, group_tab, root, depth, count, rownum as num 
		FROM (SELECT no, category, id, subject, TO_CHAR(regdate, 'YYYY-MM-DD') as dbday,
		 hit, group_id, group_step, group_tab, root, depth, (SELECT COUNT(*) FROM board_reply WHERE bno=b.no) as count 
				FROM board b 
				WHERE category=#{category} 
				ORDER BY group_id DESC,group_step ASC
			  )
	  ) WHERE num BETWEEN #{start} AND #{end}

mapper.update:
UPDATE board SET
hit=hit+1
WHERE no=#{no}

mapper.select:
SELECT no, id, subject, content, TO_CHAR(regdate,'RRRR-MM-DD') as dbday, hit
FROM board 
WHERE no=#{no}

mapper.update:
UPDATE board SET
subject=#{subject}, content=#{content} 
WHERE no=#{no}

mapper.select:
SELECT COUNT(*) 
FROM Board_Reply
WHERE bno=#{bno}

SELECT group_id,group_step,group_tab 
FROM Board_Reply
WHERE no=#{no}

mapper.update:
UPDATE Board_Reply SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE Board_Reply SET
depth=depth+1
WHERE no=#{no}

UPDATE Board_Reply SET
content=#{content}
WHERE no=#{no}

UPDATE Board_Reply SET
content=#{content}
WHERE no=#{no}

UPDATE Board_Reply SET
depth=depth-1
WHERE no=#{no}

UPDATE Board SET
depth=depth+1
WHERE no=#{no}

mapper.select:
SELECT COUNT(*) 
FROM Board
WHERE no=#{no}

SELECT group_id,group_step,group_tab 
FROM Board
WHERE no=#{no}

mapper.update:
UPDATE Board SET
group_step=group_step+1
WHERE group_id=#{group_id} AND group_step>#{group_step}

UPDATE Board SET
content=#{content}
subject=#{subject}
WHERE no=#{no}

UPDATE Board SET
content=#{content},
subject=#{subject}
WHERE no=#{no}

UPDATE Board SET
depth=depth-1
WHERE no=#{no}
