mapper.select:
SELECT name
FROM hospital
WHERE no = #{no}

SELECT dateno 
FROM hospital
WHERE no=#{no}

SELECT timeno 
FROM dateInfo
WHERE dno=#{dno}

SELECT time
FROM timeInfo
WHERE timeno=#{timeno}

SELECT name
FROM member
WHERE id=#{id}

SELECT dno,dname, dtype, TO_CHAR(dbirth, 'YYYY-MM-DD') as dbirth,dsex
FROM dog
WHERE id=#{id}
